import{_ as o,I as l,c as n,o as i,j as a,J as r,aj as s,a as c}from"./chunks/framework.C9e30fYz.js";const f=JSON.parse('{"title":"Translations","description":"Available translations for Health Checker for Joomla, and how to contribute your own.","frontmatter":{"description":"Available translations for Health Checker for Joomla, and how to contribute your own.","head":[["meta",{"property":"og:title","content":"Translations | Health Checker for Joomla"}],["meta",{"property":"og:description","content":"Available translations for Health Checker for Joomla, and how to contribute your own."}],["meta",{"property":"og:url","content":"https://www.joomlahealthchecker.com/docs/translations"}],["meta",{"name":"twitter:title","content":"Translations | Health Checker for Joomla"}],["meta",{"name":"twitter:description","content":"Available translations for Health Checker for Joomla, and how to contribute your own."}],["meta",{"name":"twitter:url","content":"https://www.joomlahealthchecker.com/docs/translations"}]]},"headers":[],"relativePath":"translations.md","filePath":"translations.md"}'),h={name:"translations.md"};function d(g,e,u,p,k,m){const t=l("CopyOrDownloadAsMarkdownButtons");return i(),n("div",null,[e[0]||(e[0]=a("div",{style:{display:"none"},hidden:"true","aria-hidden":"true"},"Are you an LLM? You can read better optimized documentation at /docs/translations.md for this page in Markdown format",-1)),e[1]||(e[1]=a("h1",{id:"translations",tabindex:"-1"},[c("Translations "),a("a",{class:"header-anchor",href:"#translations","aria-label":"Permalink to “Translations”"},"​")],-1)),r(t),e[2]||(e[2]=s(`<p>Health Checker for Joomla ships with English (en-GB) as the default language. Community-contributed translations are bundled directly into the extension package so they work out of the box.</p><h2 id="available-languages" tabindex="-1">Available Languages <a class="header-anchor" href="#available-languages" aria-label="Permalink to “Available Languages”">​</a></h2><table tabindex="0"><thead><tr><th>Language</th><th>Locale</th><th>Contributor</th><th>Link</th></tr></thead><tbody><tr><td>English</td><td>en-GB</td><td>Phil E. Taylor</td><td>Built-in</td></tr><tr><td>Russian</td><td>ru-RU</td><td><a href="https://github.com/alex-revo" target="_blank" rel="noreferrer">alex-revo</a></td><td>Built-in</td></tr><tr><td>Spanish</td><td>es-ES</td><td>Andrés Restrepo</td><td><a href="https://alamarte.com/descargas/traducciones/healthchecker" target="_blank" rel="noreferrer">alamarte.com</a></td></tr></tbody></table><h2 id="how-joomla-loads-translations" tabindex="-1">How Joomla Loads Translations <a class="header-anchor" href="#how-joomla-loads-translations" aria-label="Permalink to “How Joomla Loads Translations”">​</a></h2><p>Joomla automatically selects the correct language file based on the administrator&#39;s language preference. If a translation is missing for a particular string, Joomla falls back to the English (en-GB) version.</p><p>Each extension in the Health Checker package includes its own <code>language/</code> folder with subfolders per locale (e.g. <code>language/en-GB/</code>, <code>language/es-ES/</code>).</p><h2 id="contributing-a-translation" tabindex="-1">Contributing a Translation <a class="header-anchor" href="#contributing-a-translation" aria-label="Permalink to “Contributing a Translation”">​</a></h2><p>Want to translate Health Checker into your language? Here&#39;s how.</p><h3 id="files-to-translate" tabindex="-1">Files to Translate <a class="header-anchor" href="#files-to-translate" aria-label="Permalink to “Files to Translate”">​</a></h3><p>Each extension has one or two <code>.ini</code> files. The <code>.sys.ini</code> files contain strings used during installation and in the extension manager.</p><p><strong>Component</strong> (<code>healthchecker/component/language/{locale}/</code>)</p><ul><li><code>com_healthchecker.ini</code></li><li><code>com_healthchecker.sys.ini</code></li></ul><p><strong>Module</strong> (<code>healthchecker/module/language/{locale}/</code>)</p><ul><li><code>mod_healthchecker.ini</code></li><li><code>mod_healthchecker.sys.ini</code></li></ul><p><strong>Core Plugin</strong> (<code>healthchecker/plugins/core/language/{locale}/</code>)</p><ul><li><code>plg_healthchecker_core.ini</code> — this is the largest file, containing strings for all 130+ built-in checks</li><li><code>plg_healthchecker_core.sys.ini</code></li></ul><p><strong>Example Plugin</strong> (<code>healthchecker/plugins/example/language/{locale}/</code>)</p><ul><li><code>plg_healthchecker_example.ini</code></li></ul><p><strong>Akeeba Backup Plugin</strong> (<code>healthchecker/plugins/akeebabackup/language/{locale}/</code>)</p><ul><li><code>plg_healthchecker_akeebabackup.ini</code></li><li><code>plg_healthchecker_akeebabackup.sys.ini</code></li></ul><p><strong>Akeeba Admin Tools Plugin</strong> (<code>healthchecker/plugins/akeebaadmintools/language/{locale}/</code>)</p><ul><li><code>plg_healthchecker_akeebaadmintools.ini</code></li><li><code>plg_healthchecker_akeebaadmintools.sys.ini</code></li></ul><p><strong>mySites.guru Plugin</strong> (<code>healthchecker/plugins/mysitesguru/language/{locale}/</code>)</p><ul><li><code>plg_healthchecker_mysitesguru.ini</code></li><li><code>plg_healthchecker_mysitesguru.sys.ini</code></li></ul><h3 id="translation-rules" tabindex="-1">Translation Rules <a class="header-anchor" href="#translation-rules" aria-label="Permalink to “Translation Rules”">​</a></h3><ol><li><strong>Copy the <code>en-GB</code> files</strong> as your starting point and rename them with your locale code (e.g. <code>fr-FR</code>, <code>de-DE</code>)</li><li><strong>Keep the language keys identical</strong> — only translate the values (the part after the <code>=</code> sign)</li><li><strong>Use double quotes</strong> around values that contain special characters</li><li><strong>Do not translate</strong> placeholder tokens like <code>%s</code>, <code>%d</code>, or <code>%1$s</code> — these are replaced at runtime</li><li><strong>Preserve HTML</strong> in values — some strings contain <code>&lt;code&gt;</code>, <code>&lt;strong&gt;</code>, or <code>&lt;br&gt;</code> tags</li></ol><h3 id="file-format-example" tabindex="-1">File Format Example <a class="header-anchor" href="#file-format-example" aria-label="Permalink to “File Format Example”">​</a></h3><div class="language-ini"><button title="Copy Code" class="copy"></button><span class="lang">ini</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">; Keep the key exactly as-is, only translate the value</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">COM_HEALTHCHECKER</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;Health Checker&quot;</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">COM_HEALTHCHECKER_REPORT_TITLE</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;Site Health Report&quot;</span></span></code></pre></div><h3 id="how-to-submit" tabindex="-1">How to Submit <a class="header-anchor" href="#how-to-submit" aria-label="Permalink to “How to Submit”">​</a></h3><ul><li><strong>Pull request</strong>: Fork the <a href="https://github.com/mySites-guru/HealthCheckerForJoomla" target="_blank" rel="noreferrer">GitHub repository</a>, add your translation files under each extension&#39;s <code>language/{locale}/</code> folder, and open a PR</li><li><strong>Email</strong>: Send your translated files to <a href="mailto:phil@phil-taylor.com" target="_blank" rel="noreferrer">phil@phil-taylor.com</a></li></ul><p>Translators are credited in this documentation and in the project&#39;s release notes.</p>`,31))])}const y=o(h,[["render",d]]);export{f as __pageData,y as default};
